import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate


function UploadLink() {
  const [url, setUrl] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8001/update-link', { url });
      alert('Link updated successfully');
      // Navigate to /chatbot upon successful submission
      navigate('/chatbot');
    } catch (error) {
      alert('Failed to update link');
    }
  };

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh', // Ensure full viewport height
    backgroundImage: 'url(https://img.freepik.com/free-vector/gradient-network-connection-background_23-2148871843.jpg)',
    backgroundSize: 'cover', // Cover the entire page
    backgroundPosition: 'center', // Center the background image
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    width: '100%',
    maxWidth: '500px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)', // Optional: make form slightly opaque to increase readability
    padding: '20px', // Add some padding around the form
    borderRadius: '10px', // Optional: round the corners of the form
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Optional: add a subtle shadow to the form
  };

  const inputStyle = {
    width: '100%',
    marginBottom: '10px',
    padding: '10px',
    fontSize: '16px',
  };

  const buttonStyle = {
    width: 'auto',
    padding: '10px 20px',
    fontSize: '16px',
    cursor: 'pointer',
    background: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
  };

  return (
    <div style={containerStyle}>
      {/* Header added above the form */}
      <h2 style={{ textAlign: 'center', color: '#fff', marginBottom: '20px' }}>
        Input your tech doc to receive assistance
      </h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL"
          required
          style={inputStyle}
        />
        <button type="submit" style={buttonStyle}>Submit</button>
        {/* Button for talking with the chatbot, now removed because navigation happens upon form submission */}
      </form>
    </div>
  );
}

export default UploadLink;
