import React, { useState } from 'react';
import axios from 'axios';

function UploadLink() {
  const [url, setUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8001/update-link', { url });
      alert('Link updated successfully');
    } catch (error) {
      alert('Failed to update link');
    }
  };

  // // Inline CSS for styling
  // const formStyle = {
  //   display: 'flex',
  //   justifyContent: 'center',
  //   alignItems: 'center',
  //   height: '100vh', // Adjust as needed
  // };

  const inputStyle = {
    width: '60%', // Adjust width as needed
    marginRight: '10px', // Space between input and button
    padding: '10px', // Padding inside the input
    fontSize: '16px', // Adjust font size as needed
  };

  const buttonStyle = {
    padding: '10px 20px', // Padding inside the button
    fontSize: '16px', // Adjust font size as needed
    cursor: 'pointer', // Cursor on hover
    background: '#007bff', // Background color
    color: 'white', // Text color
    border: 'none', // Remove border
    borderRadius: '5px', // Rounded corners
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <form onSubmit={handleSubmit} style={{ width: '100%', maxWidth: '500px', margin: '0 auto' }}>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter URL"
            required
            style={inputStyle}
          />
          <button type="submit" style={buttonStyle}>Submit</button>
        </div>
      </form>
    </div>
  );
}

export default UploadLink;
