import React, { useState } from 'react';
import axios from 'axios';

function UploadLink() {
  const [url, setUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8001/update-link', { url });
      alert('Link updated successfully');
    } catch (error) {
      alert('Failed to update link');
    }
  };

  // Refining the styles
  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh', // Ensure full viewport height
  };

  const formStyle = {
    display: 'flex', // This ensures the form contents are flex items
    flexDirection: 'column', // Stack the input and button vertically
    alignItems: 'center', // Center-align the flex items (form children)
    width: '100%',
    maxWidth: '500px', // Adjust as needed
  };

  const inputStyle = {
    width: '100%', // Make input take the full width of the form
    marginBottom: '10px', // Space between input and button
    padding: '10px',
    fontSize: '16px',
  };

  const buttonStyle = {
    width: 'auto', // Adjust based on content
    padding: '10px 20px',
    fontSize: '16px',
    cursor: 'pointer',
    background: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
  };

  return (
    <div style={containerStyle}>
      <form onSubmit={handleSubmit} style={formStyle}>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL"
          required
          style={inputStyle}
        />
        <button type="submit" style={buttonStyle}>Submit</button>
      </form>
    </div>
  );
}

export default UploadLink;
